import { useSelector } from 'react-redux';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DatePicker, { registerLocale } from 'react-datepicker';
import ko from 'date-fns/locale/ko';

import styled from 'styled-components';
import Con from '../../components/common/Container';
import Button from '../../components/common/Button';
import UploadComponent from '../../components/blog/ThreePhotoUpload';
import { setBlog, getCate } from '../../../src/api/blog';
import { BasicTitle, theme } from '../../utils/theme';
registerLocale('ko', ko);

export default function addContainer() {
  const [startDate, setStartDate] = useState(new Date());
  const [category, setCategory] = useState([]);
  const [radioIndex, setRadioIndex] = useState(0);
  const [imgFile, setImgFile] = useState('');
  const [value, setValue] = useState({
    cate: 0,
    title: '',
    location: '',
    comment: ''
  });
  const userColor = useSelector(state => state.common.userColor);
  // const category = useSelector(state => state.common.category);

  console.log(category);
  const handleData = e => {
    console.log(e, 'e');
    setValue({ ...value, [e.target.name]: e.target.value });
  };

  // const handleDateChange = date => {
  //   setStartDate(date);
  // };

  const checked = (id, index) => {
    setRadioIndex(index);
    setValue({ ...value, cate: id });
  };

  const imgFormData = file => {
    setImgFile(file);
  };
  console.log(value, 'value');
  useEffect(() => {
    const getToken = localStorage.getItem('mydiary_token');
    if (getToken) {
      const config = {
        access_token: getToken
      };
      console.log('ion');
      getCate(config).then(res => {
        console.log('in');
        if (res.status === 200 && res.data) {
          console.log(res, 'res');
          setCategory(res.data.data);
        }
      });
    }
  }, []);
  const submit = () => {
    if (value.title.length > 0 && value.comment.length > 0) {
      const existFileItems = imgFile.filter(item => item !== null);
      const formData = new FormData();

      existFileItems && existFileItems.forEach(item => formData.append(`file`, item));
      const data = value;
      const numDate = Date.parse(startDate);
      data.date = numDate;
      data.cate = category && category[radioIndex].id;
      formData.append('data', JSON.stringify(data));
      console.log(formData, 'formdata');
      console.log(startDate, 'start');

      const getToken = localStorage.getItem('mydiary_token');
      if (getToken) {
        const config = {
          access_token: getToken
        };
        setBlog(config, formData).then(res => {
          console.log(res, 'res');
          // if (res.status === 200 && res.data) {
          //   console.log(res.data, 'post data');
          // }
        });
      }
    } else {
      alert('제목과 내용은 필수항목입니다.');
    }
  };
  console.log(value, 'value');
  return (
    <Contaniner>
      <Row>
        <Subject>분류</Subject>
        {category &&
          category.length > 0 &&
          category.map((item, index) => {
            return (
              <CateWrap>
                <label className="radio_container" key={index} userColor={userColor}>
                  {item.title}
                  <input type="radio" onChange={e => checked(item.id, index)} checked={radioIndex === index} />
                  <span className="checkmark" />
                </label>
              </CateWrap>
            );
          })}
      </Row>
      <Row>
        <Subject>제목</Subject>
        <Input type="text" name="title" value={value.title} onChange={handleData} />
      </Row>
      {/* <Row>
        <Subject>날짜</Subject>
        <DatePicker
          name="date"
          selected={startDate}
          onChange={handleDateChange}
          dateFormat="Pp"
          locale="ko"
          showTimeSelect
          timeFormat="HH:mm"
          showPopperArrow={false}
          showPopperArrow={false}
          timeIntervals={30}
          dateFormat="yyyy/MM/dd aa hh:mm"
          placeholderText="날짜 선택"
        />
      </Row> */}
      <Row>
        <Subject>장소</Subject>
        <Input type="text" name="location" value={value.location} onChange={handleData} />
      </Row>
      <Row>
        <div>
          <Subject>내용</Subject>
          <CountRow>
            <CountNickname>
              {MAX_COMMENT - value.comment && value.comment.length ? value.common.length : 0}
            </CountNickname>
            <Slush>/</Slush>
            <Maxcount>{MAX_COMMENT}</Maxcount>
          </CountRow>
        </div>
        <Textarea type="text" maxLength="200" name="comment" value={value.comment} onChange={handleData} />
      </Row>
      <Row>
        <Subject>사진첨부</Subject>
        <UploadComponent imgFormData={e => imgFormData(e)} />
      </Row>
      <RowRight>
        <Col>
          <SubmitBtn onClick={e => submit(e)}>저장</SubmitBtn>
        </Col>
      </RowRight>
    </Contaniner>
  );
}
const Contaniner = styled(Con)`
  display: flex;
  flex-direction: column;
  margin-top: 30px;
`;
const CateWrap = styled.div`
  @media (max-width: 768px) {
    margin-top: 15px;
  }
`;
const Row = styled.div`
  display: flex;
  justify-content: flex-start;
  padding: 20px 15px;
  box-sizing: border-box;
  align-items: center;
  @media (max-width: 768px) {
    display: block;
  }
  .end {
    margin-left: auto;
  }
  .react-datepicker__input-container input {
    height: 30px;
    font-size: 15px;
    line-height: 30px;
    padding-left: 5px;
    background: #eee;
    outline: none;
    border: 0;
    border-radius: 5px;
    ::placeholder {
      color: #000;
    }
  }
  /* Customize the label (the container) */
  .radio_container {
    display: block;
    position: relative;
    padding-left: 30px;
    margin-right: 30px;
    cursor: pointer;
    font-size: 16px;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
  }

  /* Hide the browser's default radio button */
  .radio_container input {
    position: absolute;
    opacity: 0;
    cursor: pointer;
    height: 0;
    width: 0;
  }

  /* Create a custom radio button */
  .checkmark {
    position: absolute;
    top: 0px;
    left: 0;
    height: 21px;
    width: 21px;
    background: ${props => props.userColor || '#aaa'};
    border-radius: 50%;
  }

  /* On mouse-over, add a grey background color */
  .radio_container:hover input ~ .checkmark {
    background-color: ${props => props.userColor || '#aaa'};
  }

  /* When the radio button is checked, add a blue background */
  .radio_container input:checked ~ .checkmark {
    background-color: ${props => props.userColor || '#aaa'};
  }

  /* Create the indicator (the dot/circle - hidden when not checked) */
  .checkmark:after {
    content: '';
    position: absolute;
    display: none;
  }

  /* Show the indicator (dot/circle) when checked */
  .radio_container input:checked ~ .checkmark:after {
    display: block;
  }

  /* Style the indicator (dot/circle) */
  .radio_container .checkmark:after {
    top: 7px;
    left: 7px;
    width: 6px;
    height: 6px;
    border-radius: 50%;
    background: ${props => props.userColor || '#fff'};
  }
  /* .check_container {
    display: block;
    position: relative;
    padding-left: 25px;
    margin-right: 15px;
    cursor: pointer;
    font-size: 16px;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
  }
  //check box
  /* Hide the browser's default checkbox */
`;
const Col = styled.div`
  display: inline-flex;
  svg:hover {
    fill: ${props => props.userColor};
    cursor: pointer;
  }
`;
const Subject = styled.div`
  margin-right: 20px;
  white-space: pre;
  ${BasicTitle}
`;
const Input = styled.input`
  flex: 1;
  height: 30px;
  border: none;
  background: #eee;
  border-radius: 5px;
  @media screen and (max-width: 768px) {
    width: 100%;
    margin-top: 20px;
  }
`;
const Textarea = styled.textarea`
  flex: 1;
  min-height: 200px;
  @media screen and (max-width: 768px) {
    width: 100%;
    margin-top: 20px;
  }
`;
const SubmitBtn = styled.button`
  padding: 5px 10px;
  cursor: pointer;
  font-size: 16px;
`;
const RowRight = styled(Row)`
  justify-content: center;
`;
const CountRow = styled.div`
  display: flex;
`;
const CountNickname = styled.div`
  color: #aaa;
  margin-left: 20px;
  font-size: ${theme.mFont};
`;
const Slush = styled.div`
  color: #aaa;
  margin: 0 5px 0;
  font-size: ${theme.mFont};
`;
const Maxcount = styled.span`
  color: #444;
  font-size: ${theme.mFont};
`;
const MAX_COMMENT = 200;
