import { useState, useEffect } from 'react';
import styled from 'styled-components';
import ThemeChange from '../../components/mypage/ThemeChange';
import { useSelector } from 'react-redux';
import Container from '../../components/common/Container';
import ProfileChange from '../../components/common/PhotoUpload';
import { theme, BasicTitle, BasicButton } from '../../utils/theme';
import { updateInfo, findNickname } from '../../api/user';

export default function InfoContainer(props) {
  const { showEdit, clickEditPw, clickEdit } = props;
  const userInfo = useSelector(state => state.common.userInfo);
  const userColor = useSelector(state => state.common.userColor);
  const [changeImgFile, setChangeImgFile] = useState([]);
  const [prevImg, setPrevImg] = useState('');
  const [checkTimeout, setCheckTimeout] = useState(0);
  const [nickname, setNickname] = useState('');
  const [mainTitle, setMainTitle] = useState('');
  const [nicknameCheck, setNicknameCheck] = useState(false);

  useEffect(() => {
    setNickname(userInfo && userInfo.nickname);
    setMainTitle(userInfo && userInfo.main_title);
  }, []);

  const handlePhotoChange = (file, prevImg) => {
    setChangeImgFile(file);
    setPrevImg(prevImg);
  };

  const handleTitleChange = value => {
    setMainTitle(value);
  };

  const handleNicknameChange = value => {
    setNickname(value);
    checkTimeout && clearTimeout(checkTimeout);
    const timer = setTimeout(() => {
      console.log('e');
      const getToken = localStorage.getItem('mydiary_token');
      if (getToken) {
        const config = {
          access_token: getToken
        };
        const data = { nickname: value };
        findNickname(data).then(res => {
          console.log(res, 'res');
          if (res.status === 200) {
            console.log('in');
            setNicknameCheck(true);
          }
        });
      }
    }, 2500);
    checkTimeout(timer);
  };
  console.log(value, 'value');

  // const checkDuplicateNickname = () => {
  //   prevPwdTimeout && clearTimeout(prevPwdTimeout);
  //   const timer = setTimeout(() => {
  //     console.log('e');
  //     const getToken = localStorage.getItem('mydiary_token');
  //     if (getToken) {
  //       const config = {
  //         access_token: getToken
  //       };
  //       const data = { pwd: nextValue.prevPwd };
  //       checkPwd(config, data)
  //         .then(res => {
  //           console.log(res, 'res');
  //           if (res.status === 200) {
  //             console.log('in');
  //             setPrevPwdTyping(true);
  //             setPrevPwdState(true);
  //           }
  //         })
  //         .catch(err => {
  //           if (err.response.status === 401) {
  //             setPrevPwdTyping(true);
  //             setPrevPwdState(false);
  //           }
  //         });
  //     }
  //   }, 2500);
  //   setPrevPwdTimeout(timer);
  // };
  const handleSubmit = () => {
    const formData = new FormData();
    changeImgFile && changeImgFile.forEach((item, index) => formData.append(`file`, item));
    console.log(changeImgFile, 'changeImgFile');
    console.log(formData, 'formData');
    const data = {
      nickname: nickname,
      main_title: mainTitle,
      user_color: userColor
    };
    console.log(data, 'data');
    formData.append('data', JSON.stringify(data));
    const token = localStorage.getItem('mydiary_token') && localStorage.getItem('mydiary_token');
    const config = {
      access_token: token
    };
    updateInfo(config, formData).then(res => {
      if (res.status === 200) {
        console.log(res.data, 'res');
      }
    });
  };

  return (
    <Con>
      <Column>
        <Row>
          <TopCon>
            <ProfileChange
              clickEdit={clickEdit}
              imgFormData={handlePhotoChange}
              prevImg={setPrevImg}
              showEdit={showEdit}
            />
            <MarginRow>
              <Title>닉네임</Title>
              <div>
                {showEdit && userInfo ? (
                  <Input name="nickname" defaultValue={nickname} onChange={e => handleNicknameChange(e.target.value)} />
                ) : (
                  userInfo && userInfo.nickname
                )}
                <Match>사용가능</Match> <Match>이미사용중입니다.</Match>
              </div>
            </MarginRow>
            <MarginRow>
              <Title>대표문구</Title>
              <div>
                {showEdit && userInfo ? (
                  <Input name="main_title" defalutValue={mainTitle} onChange={e => handleTitleChange(e.target.value)} />
                ) : (
                  userInfo && userInfo.main_title
                )}
              </div>
            </MarginRow>
            <MarginRow>
              <ThemeChange />
            </MarginRow>
            <Row>
              {showEdit && (
                <SubmitBtn onClick={handleSubmit} userColor={userColor}>
                  변경
                </SubmitBtn>
              )}
            </Row>
          </TopCon>
        </Row>
      </Column>
    </Con>
  );
}
const Con = styled(Container)`
  display: flex;
  justify-content: center;
`;
const Column = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  /* margin-top: 30px; */
  width: 100%;
`;
const Row = styled.div`
  display: flex;
  width: 100%;
`;
const MarginRow = styled(Row)`
  margin-bottom: 20px;
  align-items: center;
`;
const Title = styled.div`
  ${BasicTitle};
  width: 120px;
`;
const TopCon = styled.div`
  width: 100%;
  background: #fafafa;
  padding: 30px;
`;
const Input = styled.input`
  width: 120px;
  height: 20px;
`;
const SubmitBtn = styled.button`
  ${BasicButton};
  margin: 30px auto 0;
  padding: 5px 10px;
  font-size: ${theme.mFont};
`;
const Match = styled.div`
  color: ${theme.greenFont};
  margin: 10px 0 0 0;
  font-size: ${theme.sFont};
  display: flex;
  align-items: center;
`;
const Mismatch = styled.div`
  color: ${theme.redFont};
  margin: 10px 0 0 0;
  font-size: ${theme.sFont};
  display: flex;
  align-items: center;
`;
