import React, { useEffect, useState } from 'react';
import { createGlobalStyle, ThemeProvider } from 'styled-components';
import { Provider, useSelector } from 'react-redux';
import store from '../src/store';
import { theme } from '../src/utils/theme';
import 'react-image-crop/dist/ReactCrop.css';
import 'react-datepicker/dist/react-datepicker.css';
import { loginCheck } from '../src/api/auth';

const GlobalStyle = createGlobalStyle`

  html{
        width:100%;
        height:100%;
  };
  body{
        margin:0;
        padding:0;
        width:100%;
        height:100%;
        position: relative;
        font-family: ${props => props.currentValue};
        font-size:${props => props.theme.mFont};
        background:#fcfcfc;
        /* font-family: 'Noto Sans KR', sans-serif; */
        /* font-family: 'Noto Serif KR', serif; */
        /* font-family: 'Nanum Gothic Coding', monospace; */
  };
  #__next{  
            max-width:767px;
            min-height:100%;
            box-sizing:border-box;
            margin: 0 auto;
            border-left: 1px solid #dedede;
            border-right: 1px solid #dedede;
            background: #fff;
  };
  a {
      text-decoration:none; 
      outline:none;        
  };
  ul{
      list-style:none;
  };
  button{
    font-family: ${props => props.currentValue};
    :hover {
    opacity: 1;
    }
  }
  ::-webkit-input-placeholder {
    font-family: ${props => props.currentValue};
}
input{
  font-family: ${props => props.selectFont};
  font-size:${props => props.theme.mFont};
}
textarea{
  font-family: ${props => props.selectFont};
  font-size:${props => props.theme.mFont};
}
`;

export default function MyApp({ Component, pageProps }) {
  const [currentColor, setCurrentColor] = useState('#ff254f');
  const [currentValue, setCurrentValue] = useState(`'Noto Sans KR', sans-serifo'`);
  // const a = store.common.selectFont;
  // console.log(a, 'a');
  // const selectFont = store.getState().common.selectFont;
  // const selectFont = useSelector(state => state.common.selectFont);
  // console.log(selectFont, 'sel');
  // useEffect(() => {
  //   const storedToken = localStorage.getItem('mydiary_token') && localStorage.getItem('mydiary_token');
  //   const config = {
  //     access_token: storedToken
  //   };
  //   loginCheck(config).then(res => {
  //     if (res.status === 200 && res.data) {
  //       dispatch(setFont(res.data.user_font));
  //     }
  //   });
  // }, []);
  // const storedFont = useSelector(state => state.common.selectFont);
  // console.log(storedFont, 'storedFont');
  useEffect(() => {
    function handleSubscribe() {
      const newColor = store.getState().common.userColor;
      if (newColor !== currentColor) {
        setCurrentColor(newColor);
      }
    }
    const subscribeStore = store.subscribe(handleSubscribe);
    return () => subscribeStore();
  }, [currentColor]);

  function select(state) {
    console.log(state.common, 'state,common');
    return state.common.selectFont;
  }

  useEffect(() => {
    function handleChange() {
      // let newValue = currentValue;
      const newValue = store.getState().common.selectFont;

      if (newValue !== currentValue) {
        setCurrentValue(newValue);
        console.log('Some deep nested property changed from', newValue, 'to', currentValue);
      }
    }

    const unsubscribe = store.subscribe(handleChange);
    return () => unsubscribe();
  }, [currentValue]);

  return (
    <Provider store={store}>
      <GlobalStyle theme={theme} userTheme={currentColor} currentValue={currentValue} />
      <ThemeProvider theme={theme} userColor={currentColor}>
        <Component {...pageProps} />
      </ThemeProvider>
    </Provider>
  );
}
