import { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import styled from 'styled-components';
import ThemeChange from '../../components/mypage/ThemeChange';
import { useSelector } from 'react-redux';
import Container from '../../components/common/Container';
import ProfileChange from '../../components/common/PhotoUpload';
import { theme, BasicTitle, BasicButton } from '../../utils/theme';
import { updateInfo, findNickname } from '../../api/user';
import { setUserInfo } from '../../actions/base';
import store from '../../store';

export default function InfoContainer(props) {
  const { setShowEdit, showEdit, clickEditPw, clickEdit } = props;
  const userInfo = useSelector(state => state.common.userInfo);
  const userColor = useSelector(state => state.common.userColor);
  const [changeImgFile, setChangeImgFile] = useState([]);
  const [prevImg, setPrevImg] = useState('');
  const [checkTimeout, setCheckTimeout] = useState(0);
  const [nickname, setNickname] = useState(undefined);
  const [mainTitle, setMainTitle] = useState('');
  const [nicknameAvailable, setNicknameAvailable] = useState(null);
  const dispatch = useDispatch();

  const handlePhotoChange = (file, prevImg) => {
    setChangeImgFile(file);
    setPrevImg(prevImg);
  };

  const handleTitleChange = value => {
    setMainTitle(value);
  };
  useEffect(() => {
    userInfo && setNickname(userInfo.nickname);
  }, [userInfo]);

  useEffect(() => {
    userInfo && setMainTitle(userInfo.main_title);
  }, [userInfo]);

  const handleNicknameChange = value => {
    // if (value.length > 0) {
    console.log(value, 'value');
    setNickname(value);
    setNicknameAvailable(null);
    checkTimeout && clearTimeout(checkTimeout);

    if (!!value) {
      const timer = setTimeout(() => {
        const getToken = localStorage.getItem('mydiary_token');
        if (getToken) {
          const data = { nickname: value };
          findNickname(data).then(res => {
            if (res.status === 200) {
              setNicknameAvailable(true);
            } else if (res.status === 422) {
              setNicknameAvailable(false);
            }
          });
        }
      }, 2500);
      setCheckTimeout(timer);
    }
    // } else {
    //   return;
    // }
  };
  console.log(nickname && nickname.length, 'nickname');
  console.log(mainTitle, 'mainTitle');
  console.log(nicknameAvailable, 'setNicknameAvailable');

  const handleSubmit = () => {
    const formData = new FormData();
    changeImgFile && changeImgFile.forEach((item, index) => formData.append(`file`, item));
    console.log(changeImgFile, 'changeImgFile');
    console.log(formData, 'formData');
    const data = {
      nickname: nickname,
      main_title: mainTitle,
      user_color: userColor
    };
    formData.append('data', JSON.stringify(data));
    const token = localStorage.getItem('mydiary_token') && localStorage.getItem('mydiary_token');
    const config = {
      access_token: token
    };
    updateInfo(config, formData).then(res => {
      if (res.status === 200 && res.data) {
        dispatch(setUserInfo(res.data));
        clickEdit('changed');
        setShowEdit(false);
      }
    });
  };
  return (
    <Con>
      <Column>
        <Row>
          <TopCon>
            <ProfileChange
              clickEdit={clickEdit}
              imgFormData={handlePhotoChange}
              prevImg={setPrevImg}
              showEdit={showEdit}
            />
            <MarginRow>
              <Title>닉네임</Title>
              <InputCol>
                <InputRow>
                  {showEdit && userInfo ? (
                    <Input
                      name="nickname"
                      value={nickname}
                      onChange={e => handleNicknameChange(e.target.value)}
                      maxLength="10"
                    />
                  ) : (
                    userInfo && userInfo.nickname
                  )}
                  {showEdit && (
                    <CountRow>
                      <CountNickname>{nickname && nickname.length <= MAX_NICKNAME && nickname.length}</CountNickname>
                      <Slush>/</Slush>
                      <Maxcount>{MAX_NICKNAME}</Maxcount>
                    </CountRow>
                  )}
                </InputRow>
                {!!nickname && nicknameAvailable === true && <Match>사용가능</Match>}
                {!!nickname && nicknameAvailable === false && <Mismatch>이미사용중입니다.</Mismatch>}
              </InputCol>
            </MarginRow>
            <MarginRow>
              <Title>대표문구</Title>
              <InputRow>
                {showEdit && userInfo ? (
                  <Input name="main_title" value={mainTitle} onChange={e => handleTitleChange(e.target.value)} />
                ) : (
                  userInfo && userInfo.main_title
                )}
              </InputRow>
            </MarginRow>
            <MarginRow>
              <ThemeChange />
            </MarginRow>
            <Row>
              {showEdit && (
                <SubmitBtn
                  disabled={!nicknameAvailable}
                  available={nicknameAvailable === true}
                  onClick={handleSubmit}
                  userColor={userColor}
                >
                  변경
                </SubmitBtn>
              )}
            </Row>
          </TopCon>
        </Row>
      </Column>
    </Con>
  );
}
const Con = styled(Container)`
  display: flex;
  justify-content: center;
`;
const Column = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  /* margin-top: 30px; */
  width: 100%;
`;
const Row = styled.div`
  display: flex;
  width: 100%;
`;
const MarginRow = styled(Row)`
  margin-bottom: 20px;
  align-items: center;
  @media screen and (max-width: 780px) {
    display: block;
  }
`;
const Title = styled.div`
  ${BasicTitle};
  width: 120px;
`;
const TopCon = styled.div`
  width: 100%;
  background: #fafafa;
  padding: 20px;
`;
const Input = styled.input`
  width: 120px;
  height: 20px;
`;
const SubmitBtn = styled.button`
  ${BasicButton};
  margin: 30px auto 0;
  padding: 5px 10px;
  font-size: ${theme.mFont};
  cursor: ${props => (props.available === true ? 'pointer' : 'not-allowed')};
`;
const Match = styled.div`
  color: ${theme.greenFont};
  margin: 10px 0 0 0;
  font-size: ${theme.sFont};
  display: flex;
  align-items: center;
`;
const Mismatch = styled.div`
  color: ${theme.redFont};
  margin: 10px 0 0 0;
  font-size: ${theme.sFont};
  display: flex;
  align-items: center;
`;
const InputCol = styled.div`
  display: flex;
  flex-direction: column;
`;
const InputRow = styled.div`
  display: flex;
  align-items: center;
  @media screen and (max-width: 780px) {
    margin-top: 10px;
  }
`;
const CountRow = styled.div`
  display: flex;
`;
const CountNickname = styled.div`
  color: #aaa;
  margin-left: 10px;
  font-size: ${theme.sFont};
`;
const Slush = styled.div`
  color: #aaa;
  margin: 0 2px 0;
  font-size: ${theme.sFont};
`;
const Maxcount = styled.span`
  color: #aaa;
  font-size: ${theme.sFont};
`;

const MAX_NICKNAME = 10;
