import { css } from 'styled-components';
const theme = {
  white: '#ffffff',
  black: '#000000',
  ssFont: '13px',
  sFont: '15px',
  mFont: '17px',
  mlFont: '19px',
  lFont: '20px',
  xlFont: '23px',
  greenFont: '#2fae00',
  redFont: '#b30000'
};
const BasicButton = css`
  cursor: pointer;
`;

const BlueEditBtn = css`
  font-size: ${theme.lFont};
  color: #6da3f7;
  font-weight: bold;
  cursor: pointer;
`;
const BasicTitle = css`
  font-weight: bold;
  font-size: ${theme.lFont};
  color: #111;
`;
export { theme, BasicButton, BlueEditBtn, BasicTitle };
